/// by Dom Mandy in 2018


vec2 translate(vec2 p, vec2 offset) {
    return p - offset;
}


vec2 rotate(vec2 p, float angle) {
    float c = cos(angle);
    float s = sin(angle);
    mat2 R = mat2(c, -s, s, c);
    return R * p;
}


vec3 draw(vec3 buffer, float d, float r, vec3 color) {
    float up = min(iResolution.x, iResolution.y);
    d = up * d;
    r = r * up / 360.0;
  	float aa = fwidth(d);
   	return mix(buffer, color.xyz, 1.0 - smoothstep(r - aa, r, d));
}


void mainImage(out vec4 fragColor, vec2 fragCoord) {
    // Normalized pixel coordinates
    vec2 coord = fragCoord - iResolution.xy / 2.0;
    vec2 uv = coord / min(iResolution.x, iResolution.y);
    // Time varying pixel color
    vec3 col = 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0, 2, 4));
    // time
    vec2 p = uv;
    const float speed = 4.0;
    float time = speed * iTime;
    // repeat
    const float r = 1.0 / 32.0;
    const vec2 c = vec2(r);
    vec2 q = mod(p, c) - 0.5 * c;
    // distance
    float d = sdCircle(q, r / 4.0);
    // 
    vec2 m = floor(p / c);
    // color and antialias
    col = draw(vec3(0.0), d, 1.5, col);
    float t = length(m) - time;
    const float pi = 3.1415926535897932384626433832795;
    float n = mod(t, (2.0 * pi));
    float e = sdCircle((m + rotate(vec2(1.0), n)), 5.0);
    e = sin(e);

    col = mix(0.2 * col, col, 1.0 - smoothstep(-1.0, 1.0, e));
    fragColor = vec4(col, 1.0);
}

// distance function from Inigo Quilez
float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}
