// cf https://en.wikipedia.org/wiki/N-ellipse
//    https://en.wikipedia.org/wiki/Generalized_conic#Multifocal_oval_curves

void mainImage( out vec4 O,  vec2 U )
{
	vec2 R = iResolution.xy,  P[4];
    U = (U+U - R) / R.y;
    float t = iTime, d, l=0.;
    O -= O;

    P[0] = cos(t+vec2(0,1.6));                        // focal points
    P[1] = iMouse.z<=0. ? vec2(-.5) : (2.*iMouse.xy-R)/R.y;
    P[2] = vec2(.9);
    P[3] = vec2(.5,-.6);

    l = 0.;
    for (int i=0; i<4; i++)  {
        if ( i > int(mod(iTime/2.,4.)) ) break; // control N
        l += d = length(P[i]-U);                      // heart formula for N-ellipse
        O.r += d<.05 ? 2. : 0.;                       // display focal point
    }

    O += cos(30.*l);                                  // display isovalues
    //O += smoothstep(.1,0.,abs(t-3.)) -O;
}
