renderpass.code:
#define stripe(t, origin, width, s)   smoothstep(0., s, width/2. - abs(t-(origin)))

#define h2rgb(c)   clamp( abs(fract(c + vec4(3,2,1,0)/3.) * 6. - 3.) -1., 0., 1.)

#define NUM_STRIPES 7.


void mainImage( out vec4 O, vec2 U )
{
	U /= iResolution.xy;
    float s = .05, T = iTime, t;

    O -= O;

    for(float i = 1.; i <= NUM_STRIPES; i++)
        t = stripe( U.x,
                    .5 + .25* sin( .4* cos(T*i) * (i-4.+ 2.*U.y + .04*sin(T) -.5) ) ,
                    .1, s),
        O = mix(O, h2rgb( (i-4.) / NUM_STRIPES ), t);
}
