vec2 shapeWave(float theta,float rot, int n){
    float t = mod(theta, 2.*3.14159/float(n));
    float p = (1./2.-1./float(n))*3.14159;
    float r = tan(t)/tan(p);
    float l = 1./cos(t)/(r+1.);
    return vec2(sin(theta+rot),cos(theta+rot))*l;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy-.5)* iResolution.xy / iResolution.xx;
    float d = length(uv);
    float t = atan(uv.y,uv.x);
    int n = int(5.+2.*sin(.4*iTime));
    fragColor = vec4(0,0,0,1);
	fragColor += vec4(1.-smoothstep(.5*iResolution.x*abs(d-
                                     .1*length(shapeWave(t+iTime*2.,0., n))
                                    )
        ,0.,1.));
	fragColor += vec4(1.-smoothstep(.5*iResolution.x*abs(uv.y-.1*shapeWave(uv.x*10.,iTime*2., n).x ),0.,1. ));
	fragColor += vec4(1.-smoothstep(.5*iResolution.x*abs(uv.x-.1*shapeWave(uv.y*10.,iTime*2., n).y ),0.,1. ));

}

vec2 shapeWave(float theta,float rot, int n){
    float t = mod(theta, 2.*3.14159/float(n));
    float p = (1./2.-1./float(n))*3.14159;
    float r = tan(t)/tan(p);
    float l = 1./cos(t)/(r+1.);
    return vec2(sin(theta+rot),cos(theta+rot))*l;
}

vec2 mainSound( in int samp, float time )
{
    int n = int(5.+2.*sin(.4*time));
    return .2*shapeWave(6.2831*440.0*time,0.,n);
}
