float nubes(vec2 uv, float rad, vec2 pos) {
   	uv.y += 0.09*cos(9.0*uv.x + 2.5*iTime);
   return 1.-smoothstep(rad,rad+0.60,length(uv-pos + 0.23*uv.y)); 
}

float pradera(vec2 uv, float rad, vec2 pos, float offset) {
   return 1.-smoothstep(rad,rad+offset,length(uv-pos));
}

float cac(vec2 uv, vec2 pos) {
    uv -= pos;
    float rad = sin(9.0*atan(uv.y,uv.x) + 0.7*cos(5.0*uv.y + 4.0*iTime));
   return 1.-smoothstep(0.38*rad,0.38*rad+0.01,length(uv+pos));
}

float cac2(vec2 uv, vec2 pos) {
    uv -= pos;
    float rad = 1.2*cos(6.0*atan(uv.y,uv.x) - 10.0*uv.x - sin(uv.y+5.0*iTime));
   return 1.-smoothstep(0.25*rad,0.25*rad + 0.01,length(uv));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0*uv;
    p.x *= iResolution.x/iResolution.y;

    //Colores
    vec3 azl = vec3(0.2588, 1.0, 0.8784);
    vec3 ock = vec3(0.9490, 0.9686, 0.6980);
    vec3 cfe = vec3(0.7137, 0.7294, 0.5411);
    vec3 bck = vec3(0.6117, 0.5137, 0.1215);
    vec3 pln = vec3(0.6705, 0.2784, 0.0);
    vec3 nb = vec3(0.8196, 1.0, 0.8862);
    vec3 col = vec3(0.0);

    //Nubes,pradera,hierba
    float pra1 = pradera(p,1.0,vec2(-1.4,-1.6),0.03);
    float pra2 = pradera(p,1.0,vec2(1.4,-1.6),0.03);
    float pra = pradera(p,2.0,vec2(0.0,-2.3),0.01);
	float ca1 = cac(p,vec2(0.0,-0.4));
    float ca2 = cac2(p,vec2(1.0,-0.64));
    float ca3 = cac2(p,vec2(-1.0,-0.64));

    //Movimiento nbs
    float nub1 = nubes(3.0*p, 0.44, vec2(4.0, 1.5 + 0.1*cos(p.y + iTime)));
    nub1 += nubes(3.0*p, 0.64, vec2(0.5, 2.5 + 0.2*sin(p.y + iTime)));
    nub1 += nubes(3.0*p, 0.45, vec2(-3.4, 1.3 + 0.1*cos(p.y + iTime)));

   	col = mix(ock,azl,p.y);
    col = mix(col,cfe,pra1);
    col = mix(col,cfe,pra2);
    col = mix(col,bck,pra);
    col = mix(col,pln,ca1);
    col = mix(col,pln,ca2-(pra*ca2));
    col = mix(col,pln,ca3-(pra*ca3));
    col = mix(col,nb,nub1);

    fragColor = vec4(col,1.0);
}
