// simplified by @FabriceNeyret2 -- thanks!

void mainImage( out vec4 O, vec2 U )
{
    float t = iTime;
    vec2 point = .5 + vec2(cos(t), sin(t))/4.,
	     uv    = U / iResolution.xy,
         p     = abs(point - uv);
    float rectdist = p.x + p.y,
          eucdist  = length(p) + fract(-t/16.),
          dist     = mix(rectdist, eucdist, sin(t*4.));
	O = texture(iChannel0,vec2(dist,t/16.));
}

