#define BLUR_SAMPLES 128
#define BLUR_RADIUS .025
#define BLUR_BRIGHTNESS .7

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 px = iResolution.xy / 4.;
    vec2 uv = floor((fragCoord/iResolution.xy)*px)/px;

    fragColor = texture(iChannel0, uv);
    fragColor.a = 1.;

    vec2  c = (iResolution.xy*vec2(.2,.8))/iResolution.xy;
    vec3  s = vec3(0.);
    float w = 1./float(BLUR_SAMPLES);
    for(int i = 0; i < BLUR_SAMPLES; i++) {
        s  += texture(iChannel0,uv).a;
        uv += (c-uv) * BLUR_RADIUS;
    }
    fragColor.xyz += s*w*BLUR_BRIGHTNESS;
    fragColor.xyz  = clamp(fragColor.xyz,0.,1.);
}

#define SET(col) fragColor.xyz = col; fragColor.a = 0.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord/iResolution.xy;
    float xPos = iTime*.25;

    fragColor.a   = clamp(1.-(length(iResolution.xy*vec2(.2,.8)-fragCoord)-30.),0.,1.);
    fragColor.xyz = vec3(.1,.1,.3);
    fragColor.xyz += vec3(.5,.5,1.) * fragColor.a;

    if(uv.y < texture(iChannel0, vec2(uv.x+xPos*.1,0.)).x*.8) {
        SET(vec3(.1));
    }
    if(uv.y < texture(iChannel0, vec2(uv.x+xPos*.3,cos(uv.x+xPos*.3)*.5+.5)).x*.7) {
        SET(vec3(.2));
    }
    if(uv.y < texture(iChannel0, vec2(uv.x+xPos*.5,sin(uv.x+xPos*.5)*.5+.5)).x*.9) {
        SET(vec3(.5));
    }
}
