void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texelFetch(iChannel1, ivec2(fragCoord),0);
}

#define SRC_CHAN iChannel1

/* From https://en.wikipedia.org/wiki/Conway%27s_Game_of_Life :
   Any live cell with fewer than two live neighbors dies, as if by underpopulation.
   Any live cell with two or three live neighbors lives on to the next generation.
   Any live cell with more than three live neighbors dies, as if by overpopulation.
   Any dead cell with exactly three live neighbors becomes a live cell, as if by reproduction.
 */
const bool onalive[9] = bool[9](false,false,true,true,false,false,false,false,false);
const bool onded[9] =   bool[9](false,false,false,true,false,false,false,false,false);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    ivec2 coord = ivec2(fragCoord);
    int neighbours = int(
        		 texelFetchOffset(SRC_CHAN,coord,0,ivec2(1)).r + 
   			     texelFetchOffset(SRC_CHAN,coord,0,ivec2(-1)).r +
				 texelFetchOffset(SRC_CHAN,coord,0,ivec2(1,0)).r +
				 texelFetchOffset(SRC_CHAN,coord,0,ivec2(1,-1)).r +
				 texelFetchOffset(SRC_CHAN,coord,0,ivec2(0,1)).r + 
				 texelFetchOffset(SRC_CHAN,coord,0,ivec2(0,-1)).r + 
				 texelFetchOffset(SRC_CHAN,coord,0,ivec2(-1,1)).r + 
		  		 texelFetchOffset(SRC_CHAN,coord,0,ivec2(-1,0)).r);
    fragColor = vec4(vec3(texelFetch(SRC_CHAN,coord,0).r != 0.0 ? onalive[neighbours] : onded[neighbours]),1.0);

  	if (iFrame < 120) {
    	fragColor = vec4(length(fwidth(step(0.5,texture(iChannel3, fragCoord/iResolution.xy)))));
  	}
}
