void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    vec2 myuv = vec2(uv-vec2(0.5,0.5));
    vec2 notmyuv= vec2(myuv.x*3.0,myuv.y*1.8);
    float balanco=sin(iTime)*0.01+sin(uv.x*3.0+iTime)*0.01;

    float mag = length(notmyuv);



    if (round(mag)==0.0)
    	fragColor = vec4(round(mag),round(mag),1,0);
    else
        fragColor = vec4(0,1,0,0);

    if(uv.y<(0.66+balanco))
        fragColor=vec4(0,1,0,0);
    if(uv.y<(0.70+balanco) && uv.y>(0.30+balanco))
        fragColor=vec4(1,1,0,0);
    if(uv.x<(0.67+balanco) && uv.x>(0.32+balanco))
        fragColor=vec4(1,1,0,0);
    if(uv.y<(0.01+balanco))
        fragColor=vec4(1,0,1,0);
    if(uv.y>(0.98+balanco))
        fragColor=vec4(1,0,1,0);
    if (round(mag+balanco)==0.0)
    	fragColor = vec4(round(mag),round(mag),1,0);


}
