renderpass.code:
#define V1 float
#define V2 vec2
#define V3 vec3
#define V4 vec4

#define T iTime
#define R iResolution.xy
#define D ( 1. / R )

V1
  amp   = .4,
  count = 4.,
  A     = 40.,
  S     = .010,
  C     = .011;
V4 rct = V4( -6.28, -3., 2. * 6.28, 5. );

V2 cvt( V2 p ) { return rct.xy + ( p * D ) * rct.zw; }


V1 fct( V1 x, int id, float count ) {

    V1
    s1 = sin(S * T),
    c1 = cos(C * T),
    e  = exp( -.125 * x * x ),
    s2 = sin(A * x * s1) * e,
    c2 = cos(A * x * c1) * e;

    return
        id < 2
        	? id < 1
        		? +0.5 + amp * c2
        		: -0.5 + amp * s2
        	: id < 3
        		? -2.0 + amp * (c2 + s2)
                : +1.5 + amp * c2 * s2;
}

V4 plt( V2 p, int id, float count ) {

    V1 f0 = fct( p.x ,id, count );

    V2 df = V2( D.x, .125 * ( fct( p.x + 4. * D.x, id, count ) - fct( p.x - 4. * D.x, id, count ) ) ),
       dy = V2( 0, p.y - f0 ),
       p1 = p - V2( p.x, f0 ) - df * dot( df, dy ) / dot( df, df );

    V1 l = length( p1 );

    return smoothstep( 1., 0., l * .1 * R.y ) * (
        id < 2
        	? id < 1
        		? V4( 1., 0., 0.,  1. )
        		: V4( 0., 1., 0.,  1. )
        	: id < 3
        		? V4( 1., 1., 0.,  1. )
        		: V4( .25, .5, 1.,  1. )
    	);
}


void mainImage( out V4 o, V2 i ) {

    i = cvt( i );

    o = plt( i, 0, count );

    for( int j = 1; j < int( count ); ++ j ) {

        o += plt( i, j, count );
    }
}
