void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	vec3 backGround = vec3(1.0,0.0,1.0);
    vec4 text = texture(iChannel0,uv);
    vec2 center  = vec2(0.5*iResolution.x/iResolution.y,0.5);
	float var = abs(sin(iTime)) ;
    vec2 mouse = iMouse.xy/iResolution.xy;

    float ambiant = 0.1;

    float diffuse ;
    vec3 normal = normalize(vec3(0.0,0.0,1.0));        
    vec3 FragPos = vec3(uv,0.0);
	vec3 lightPos = vec3(var*2.,1.,1.0);
    vec3 lightDir = normalize(lightPos - FragPos);  
	diffuse = max(dot(normal, lightDir), 0.0);


    float specular;
    float specularStrength = 1.0;
    float shininess = 64.;
    vec3 viewPos = vec3(0.0,0.0,1.0);
    vec3 reflectDir = reflect(-lightDir, normal);  
	vec3 viewDir = normalize(viewPos - FragPos);
    float spec = pow(max(dot(viewDir, reflectDir), 0.0), shininess);
	specular  =spec * specularStrength;



    vec3 color = (ambiant + diffuse +specular )* (text.rgb);
    fragColor = vec4(color,1.0); 
 }
