void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = mix(textureLod(iChannel0,uv,6.),textureLod(iChannel0,uv,0.),.2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float R=iResolution.x*.25;
    float r=length(fragCoord.xy-iResolution.xy*.5);
    fragColor.xyz=vec3(0,0,0)+smoothstep(R-.7,R+.7,r);
    fragColor.w=1.;
}
