#define speed .25 

vec3 color = vec3(1.0, .0, 1.0);
vec4 backGround = vec4(1.0);


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	float pi = 3.14;
	vec2 uv = fragCoord.xy;
	vec2 center =  iResolution.xy * 0.5;
    float var = iTime*speed;//abs(sin(speed*iTime)); 
	float radius = iResolution.y  *var ;

    float d = length(center - uv) - radius;
	float t = clamp(d, 0.0, 1.0);
	vec4 layer2 =  vec4(color, (1.0 - t));

    float slope = abs(sin(d*.1)) ;
 	layer2.a *= slope; 

 	fragColor = mix(backGround, layer2, layer2.a);
}
