void mainImage( out vec4 O,  vec2 U )
{
	U /= iResolution.xy;
    float v = U.x*12.6;

    O -= O;

    for (int i=0; i<60; i++)
        v = sin(v),                                // recursion
     // v = cos(7.*U.x*v), 
     // v = cos(3.14*U.x*v) *.9,
     // v = cos(7.*iMouse.y/iResolution.y*v),
     // v = sin(v*(1.+.3*sin(iDate.w))),   
	 // O += 1e-3/abs(2.*U.y-1. - v);  // 130 chars
	    O = smoothstep(.02,.0,abs(2.*U.y-1. - v)) + O*vec4(.99,.98,.97,1); // curve + relaxation
}
