// based on https://www.shadertoy.com/view/XtV3Wz with points aniomation

//* 238c by Coyote 
void mainImage( out vec4 f, vec2 g )
{
    vec2 p = iResolution.xy; f=iDate;

    g += g-p; g /= dot(g,g)/p.y*.1; g.x += f.w; // uv

	for(float i=0.;i<9.;i++)
        p.x = mod(i, 3.), p.y = (i-p.x)/3., // lattice point coords
        p -= fract(g),
        f = min(f, length( 
            p - .5 // lattice point // --p not work on ati card 
             + .5 * sin( iDate.w + 9.*fract( sin((g+p) * mat2(2,5,5,2))) ) // hash (uv offset)
             )); // uv
}/**/

