float rnd(vec2 uv) { return fract(cos(iTime*uv.x-iTime*uv.y)*1e5); }
float rndPIX(vec2 fc) {return rnd(vec2(0.0001*fc.x,0.01*fc.y));}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float A = rndPIX(fragCoord.xy);
    float B = rndPIX(fragCoord.xy);
    float C = rndPIX(fragCoord.xy);
	fragColor = vec4(A,B,C,0.0);
}

#define LYA 55.00

float rnd(vec2 uv,float time) { return (fract(cos(time*15.0))*1e5); }
float rndPIX(float time) {return rnd(vec2(0.001,0.01),time);}

vec2 mainSound( in int samp,float time)
{
    float fq1 = time * rndPIX(time);
    float amp = sin(fq1);
    return (vec2(sin(time*LYA*0.62) * amp) + rndPIX(time))*0.1;
}
