#define PI 3.14159

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 pos = fragCoord.xy / iResolution.xy;	// [0,1] +x right, +y up
    pos.y = pos.y * 2.0 - 1.0; // x[0,1] y[-1,+1]

    // animate the sine wave properties
    float amplitude = abs(sin(iTime));
    float frequency = 0.5 + 5.0 * sin(iTime) * sin(iTime);
    float phase = 10.0 * iTime;

    vec3 colour = vec3(0.0);
    float x = 2.0*PI*frequency*pos.x + phase;
	float val = amplitude * sin(x);
    float delta = abs(val-pos.y);

    // extra thickness is proportional to the magnitude of the curve gradient 
    float thickness = 0.06 + 0.04 * frequency * abs(cos(x));
    colour.g = min(1.0-(1.0/thickness)*delta, 1.0); // remap delta [0,thickness] to [1,0]

    fragColor = vec4(colour,1.0);
}
