void ADD ( vec4 inp, out vec3 COL ) {
    COL = mix(COL,inp.rgb,inp.w);
}
vec4 nyan(vec2 uv, float i) {
    i=mod(floor(i), 6.);

    float j = 40./256.;
    uv.x*=j;
    return texture(iChannel0, uv+vec2(j,0)*i);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 COL = vec3(1.-length(uv-.5)/3.82, 0., .2);
    ADD(nyan(uv, iTime*10.)-length(uv-.5)/1.62, COL);
	fragColor = vec4(COL,1.0);
}
