float number = 2.0;

float moddetermine(vec2 uv)
{
    uv = floor(uv * 2.0);

    return mod(uv.x + uv.y, 2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy * number;


    vec3 newColor = vec3(moddetermine(uv));

	fragColor = vec4(newColor, 1.0);
}
