#define PI 3.1415926535897932384626433832795

const vec4 deepblue = vec4(0.05, 0.3, 0.74, 1.0);
const vec4 midblue = vec4(0.12, 0.36, 0.85, 1.0);
const vec4 whiteblue = vec4(0.5, 0.76, 0.94, 1.0);

void mainImage(out vec4 fragColor, in vec2 fragCoord){
	vec2 uv = (fragCoord.xy / iResolution.xy - vec2(0.5, 0.5)) * 4.0;
    uv.x += cos(iTime * 0.25) * 0.2;
    uv = uv + uv * sin(iTime * 0.2) * 0.1;
    float waves1 = abs(cos(uv.x * cos(uv.y / PI) * PI) + sin(uv.y * PI + iTime)) / 2.0;
    float waves2 = (cos(sin(uv.x - uv.y) + iTime) + sin(uv.x * uv.x * uv.y)) / 2.0;
    vec4 color = midblue;
    if(waves2 < 0.125) color = deepblue;
    if(waves1 < 0.125) color = whiteblue;
	fragColor = color;
}
