void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 SumColor;
    vec2 uv = fragCoord.xy / iResolution.xy;
    SumColor+= texture(iChannel0, uv).xyz;

    uv *= iChannelResolution[0].xy;

    vec2 newUV = (uv + vec2(1,0)) / iChannelResolution[0].xy;
    SumColor += texture(iChannel0, newUV).xyz;

    newUV = (uv + vec2(-1,0)) / iChannelResolution[0].xy;
    SumColor += texture(iChannel0, newUV).xyz;

    newUV = (uv + vec2(2,0)) / iChannelResolution[0].xy;
    SumColor += texture(iChannel0, newUV).xyz;

    newUV = (uv + vec2(-2,0)) / iChannelResolution[0].xy;
    SumColor += texture(iChannel0, newUV).xyz;

    newUV = (uv + vec2(0,1)) / iChannelResolution[0].xy;
    SumColor += texture(iChannel0, newUV).xyz;

    newUV = (uv + vec2(0,-1)) / iChannelResolution[0].xy;
    SumColor += texture(iChannel0, newUV).xyz;

    newUV = (uv + vec2(0,2)) / iChannelResolution[0].xy;
    SumColor += texture(iChannel0, newUV).xyz;

    newUV = (uv + vec2(0,-2)) / iChannelResolution[0].xy;
    SumColor += texture(iChannel0, newUV).xyz;


    SumColor /= 9.;

    fragColor = vec4(SumColor,1.0);
}
