vec4 backColor = vec4(1.0,1.0,1.0,1.0);

//Rectangle
float recWidth = 0.4;
float recHeight = 0.02;
vec2 recPos = vec2(0.8,0.2);
vec4 recColor = vec4(0.7,0.0,0.0,0.9);

//Rectangle
float rec2Width = 0.1;
float rec2Height = 0.2;
vec2 rec2Pos = vec2(.95,0.0);
vec4 rec2Color = vec4(1.0,0.0,0.0,0.7);

//Circle 1
float circ1Rad = 0.5;
vec2 circ1Pos = vec2(0.9,0.5);
vec4 circ1Color = vec4(0.5,1.0,0.5,1.0);

//Circle 2
float circ2Rad = 0.2;
vec2 circ2Pos = vec2(0.65,0.6);
vec4 circ2Color = vec4(0.3,0.3,1.0,0.7);

//Circle 3
float circ3Rad = 0.15;
vec2 circ3Pos = vec2(1.2,0.7);
vec4 circ3Color = vec4(1.0,0.3,1.0,0.7);

//Circle 4
float circ4Rad = 0.05;
vec2 circ4Pos = vec2(1.2,0.7);
vec4 circ4Color = vec4(1.0,1.0,1.0,1.0);

//Circle 5
float circ5Rad = 0.05;
vec2 circ5Pos = vec2(0.65,0.6);
vec4 circ5Color = vec4(1.0,1.0,1.0,1.0);

//Layers
vec4 Layer1;
vec4 Layer2;
vec4 Layer3;
vec4 Layer4;
vec4 Layer5;
vec4 Layer6;
vec4 Layer7;
vec4 Layer8;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    uv.x *= (iResolution.x / iResolution.y);

    fragColor = backColor;

    float loc1 = sin(iTime) * .1;
    float loc2 = sin(iTime) * .15;
    float loc3 = sin(iTime * 0.5) * .05;

    Layer1 = backColor;

    //Circle 1
    if(length(uv - circ1Pos) <= circ1Rad)
    {
		Layer2 = circ1Color;
    }

    //Circle 2
    if(length(uv - circ2Pos) <= circ2Rad)
    {
		Layer3 = circ2Color;
    }

    //Circle 3
    if(length(uv - circ3Pos) <= circ3Rad)
    {
		Layer4 = circ3Color;
    }

    //Rectangle
    if(uv.x >= recPos.x && uv.x <= recPos.x + recWidth)
    {
		if(uv.y >= recPos.y && uv.y <= recPos.y + recHeight)
    	{
			Layer5 = recColor;
    	}
    }

    //Rectangle 2
    rec2Height += loc1;
    if(uv.x >= rec2Pos.x && uv.x <= rec2Pos.x + rec2Width)
    {
		if(uv.y >= rec2Pos.y + rec2Height && uv.y <= rec2Pos.y + 2.0 * rec2Height)
    	{
			Layer8 = rec2Color;
    	}
    }

    //Circle 4
    circ4Pos.x += loc1;
    circ4Pos.y += loc3;
    if(length(uv - circ4Pos) <= circ4Rad)
    {
		Layer6 = circ4Color;
    }

    //Circle 5
    circ5Pos.x += loc2;
    circ5Pos.y += loc3;
    if(length(uv - circ5Pos) <= circ5Rad)
    {
		Layer7 = circ5Color;
    }

    //Layer Blending
    vec4 Composite = mix(Layer1, Layer2, Layer2.a);
    Composite = mix(Composite, Layer3, Layer3.a);
    Composite = mix(Composite, Layer4, Layer4.a);
    Composite = mix(Composite, Layer5, Layer5.a);
    Composite = mix(Composite, Layer6, Layer6.a);
    Composite = mix(Composite, Layer7, Layer7.a);
    //Composite = mix(Composite, Layer8, Layer8.a);

    fragColor = Composite;
}
