void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float theta = 1.3;
    float sinTheta = sin(theta);
    float cosTheta = cos(theta);

    uv -= vec2(0.5, 0.5);
    uv -= vec2(sinTheta, cosTheta) * mod(iTime * 0.2, 0.0);

    mat2 rot = mat2( cosTheta, -sinTheta, sinTheta, cosTheta );

    uv = uv * rot;

    vec2 tile = mod(uv * vec2(5.0, 3), 1.0);
    vec2 mask1 = mod(uv * vec2(5.0, 30.0), vec2(2.0, 15.0));

    tile.xy = tile.yx;

    if (mask1.x > 1.0 || mask1.y > 5.0)
    	tile.y = 1.0 - tile.y;

    vec4 color = texture(iChannel0, tile * vec2(0.32, 1.0) + vec2(0.0, 0.1));

    fragColor = color;
}
