vec4 line( vec2 uv, float position, float width ) {
	float h = width / 2.0;
    float top = position + h;
    float bottom = position - h;

    return vec4(step(bottom, uv.y) - step(top, uv.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float position = 0.5;
    if (iMouse.z > 0.0) {
        position = iMouse.y / iResolution.y;
    }

    float width = 2.0;
    float p = 1.0 / iResolution.y;
    float w = width * p; // Normalized width.

    fragColor = line(uv, position, w);
}
