void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 xy = fragCoord.xy / iResolution.xy; // get screen coordinates x & y in one line

    vec4 textColor = texture(iChannel0, xy); // get the pixel at xy from iChannel 0

    // any variable with i in from is a ShaderToy variable


    //create a new var that is the dot product of textColor.rgb and a new Vec3
    //float deSat = dot(textColor.rgb,vec3(.3, .59, .11));

    //this is the same as a dot product. 
	float gs = textColor.r*0.3+textColor.g*0.6+textColor.b*0.1;
    vec4 finalColor = vec4(gs, gs, gs, 1.0); 
	fragColor = finalColor;
}
