#define PI 3.14159
float circle(vec2 p, vec3 rr)
{
    float theta = atan(p.y,p.x);
    float r     = length(p);
    float i = round(((r-rr.y)/rr.x - (rr.z+theta))/(2.*PI));
    return abs(r - (rr.z + theta + float(i)*2.0*PI) * rr.x) - rr.y;
}
vec3 render(vec3 dst, vec3 src, float d)
{
    float dd = 1.0-smoothstep(-0.02,0.00,d);
    dst = mix(dst, src, dd);
    return dst;
}
vec3 draw(vec2 p)
{
    vec3 color = texture(iChannel0,p).rgb;
    vec3 c = vec3(1.0);
    color = render(color,c,circle(p, vec3(0.01,4./iResolution.y,2.0*PI*fract(iTime))));
    return color;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p  = (2.*fragCoord - iResolution.xy) / iResolution.y; 
	vec3 col = draw(p);
    fragColor = vec4(col,1.0);
}
