vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec2 randDisplacement() {
    return vec2(fract(sin(iTime*14237.14238)*23403.24234),
                fract(sin(iTime*35034.24809)*23473.42303));
}

vec2 gradient(float c) {
    float w1 = 1.0-mod(c, 0.02)*50.0;
    float bri;
    float sat = 1.0;
    if (w1<0.9) {
        bri = 1.0 - w1/0.9 + 0.2;
    }
    else {
        bri = (w1-0.9) / 0.1 + 0.2;
    }
    if (bri>1.0) {
        sat = 1.0 - (bri-1.0)/0.5;
    }
    return vec2(min(1.0, bri), sat);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {	

	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv*2.0 - 1.0;
    uv *= 0.7+0.3*sin(iTime*14.2354);
    uv.x *= iResolution.x / iResolution.y;
    float theta = iTime*10.0;
    mat2 rot = mat2 (cos(theta), sin(theta), -sin(theta), cos(theta));
    uv *= rot;
    uv += 0.4*randDisplacement();

    vec2 z = uv;
    vec2 c = vec2(-0.73+0.05*cos(30.5142*iTime+0.3),0.3+0.1*sin(10.0*iTime+0.9));

    float minDist = 100.0;
    float iterations = -1.0;
    for (float i = 0.0; i < 200.0; i++) {
        z = vec2( z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + c;
        if (abs(z.y)<minDist) {
            minDist = abs(z.y);
        }
        if (dot(z,z) > 256.0) {
            iterations = i;
            break;
        }
    }

    iterations = iterations + 2.0 - log(log(length(z)))/log(2.0);

    float hue = pow(iterations/50.0,0.4+0.2*sin(4.534243*iTime));
    hue += iTime*10.0;

    if (iterations >= 0.0) {
        vec2 brisat = gradient(hue);
		fragColor = vec4(hsv2rgb(vec3(hue,brisat.y,brisat.x)) ,1.0);
    }
    else {
        fragColor = vec4(min( vec3(1.0), (1.0-minDist*200.0)*vec3(1.0,1.2,2.0) ), 1.0);
    }
}
