float numCols = 8.0;
float numRows = 8.0;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
// Normalized pixel coordinates (from 0 to 1)
	vec2 uv = fragCoord/iResolution.xy;

	vec4 newColor = vec4(1.0,1.0,1.0,1.0); //white color

	float rows = mod(floor(uv.y * numRows), 2.0); //row creation
    float cols = mod(floor(uv.x * numCols), 2.0); //col creation

	if(rows == 0.0 && cols == 0.0) //color placement
	{
		fragColor = newColor;
	}
    else if (rows != 0.0 && cols != 0.0)
    {
        fragColor = newColor;
    }
    else
	{
		fragColor = vec4(0.0,0.0,0.0,1.0); //black color
	}
}
