void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float band = floor(fragCoord.y / 32.0) + iTime * 0.1;
	fragColor = vec4(mod(fragCoord.y - 8.0, 32.0) > 16.0 ? vec3(0.0) : mix(vec3(0.1, 1.0, 0.7), vec3(0.2, 0.3, 0.25), sin(band * 3.9 + fragCoord.x / (8.0 + sin(band * 4.0 + 3.1) * 18.0))),1.0);
}
