void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
    float t = iTime;

    float zoom = 0.65 + 0.38*cos(0.3*t);
    zoom = pow( zoom,8.0);
    uv *= zoom;
    float x = -1.1649;
    float y = 0.25587;
    uv += vec2(x - x * zoom,y - y * zoom);
    //uv += vec2(x,y);

    float def = 0.02;
    float scale = (1.0/(t*10.0));
    scale = scale < def ? scale > 0.00001 ? scale : 0.00001 : def;

    vec2 z = vec2(0.0);
    vec3 c = vec3(0.0);
    float val = 0.0;

    for(int i=0; i < 512; i++)
    {
        if( (z.x*z.x + z.y*z.y) >= 4.0 ) break;
        z = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + uv;
        if( (z.x*z.x + z.y*z.y) >= 2.0 )
        {
        	c = vec3(sin(float(i)*0.13));
        }
    }

	fragColor = vec4(c,1.0);
}
