void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

	fragColor += vec4(texture(iChannel0,vec2(0.5*uv.x*sin(iTime)+0.5,0.5*uv.y*cos(iTime)+0.5)));

    fragColor.xy -= rotate(vec2(fragColor.x*cos(iTime*0.1),fragColor.y*sin(iTime*0.1)), cos(iTime)*0.1);
    if(ceil(distance(uv, vec2(0.0,0.0)))>0.8)
    {
    	fragColor += vec4(texture(iChannel1,vec2(0.5*uv.x*sin(iTime)+0.5,0.5*uv.y*cos(iTime)+0.5)));
    }
    vec4 ninja = vec4(texture(iChannel2,uv*uv*0.5));
    if(ninja.y < 0.45 || length(ninja) > 1.3 )
    {
        fragColor = ninja;
    }
}
