#define STEPS 30
#define FAR 40.0
#define EPSILON 0.001
#define PIXELR 0.5/iResolution.x
#define PI 3.14159265359

#define BOUNCES 3
#define SAMPLES 3.0

//Hash methods from https://www.shadertoy.com/view/4djSRW
//#define HASHSCALE3 vec3(.1031, .1030, .0973)
#define HASHSCALE3 vec3(443.897, 441.423, 437.195)

vec2 hash23(vec3 p3){
	p3 = fract(p3 * HASHSCALE3);
    p3 += dot(p3, p3.yzx+19.19);
    return fract((p3.xx+p3.yz)*p3.zy);
}
vec3 hash33(vec3 p3){
	p3 = fract(p3 * HASHSCALE3);
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.xxy + p3.yxx)*p3.zyx);

}

//from http://amietia.com/lambertnotangent.html
vec3 lambertNoTangent(vec3 normal, vec2 uv){
   float theta = 6.283185 * uv.x;
   uv.y = 2.0 * uv.y - 1.0;
   vec3 spherePoint = vec3(sqrt(1.0 - uv.y * uv.y) * vec2(cos(theta), sin(theta)), uv.y);
   return normalize(normal + spherePoint);
}

//Distance functions from Mercury's SDF library
//http://mercury.sexy/hg_sdf/

// Maximum/minumum elements of a vector
float vmax3(vec3 v) {
	return max(max(v.x, v.y), v.z);
}

float fSphere(vec3 p, float r) {
	return length(p) - r;
}

// Box: correct distance to corners
float fBox(vec3 p, vec3 b) {
	vec3 d = abs(p) - b;
	return length(max(d, vec3(0))) + vmax3(min(d, vec3(0)));
}

float pReflect(inout vec3 p, vec3 planeNormal, float offset) {
	float t = dot(p, planeNormal)+offset;
	if (t < 0.0) {
		p = p - (2.0*t)*planeNormal;
	}
	return sign(t);
}

float fOpUnionRound(float a, float b, float r) {
	vec2 u = max(vec2(r - a,r - b), vec2(0));
	return max(r, min (a, b)) - length(u);
}

vec2 dist(vec3 p){

    vec3 pp = p-vec3(0.0, 0.0, 6.0);

    float ball = fSphere(p-vec3(0.0, -4.75, 0.0), 1.25);
    float jalusta = fBox(p-vec3(0.0, -8.0, 0.0), vec3(1.5, 2.0, 1.5));
    float patsas = fOpUnionRound(ball, jalusta, 0.5);

    pReflect(pp, vec3(0.0, 0.0, 1.0), 6.0);
    float cube = fBox(pp, vec3(1.0, 10.0, 1.0));

    float box = -fBox(p, vec3(10.0, 10.0, 10.0));

    float scene = min(patsas, min(cube,box));

    float id = 0.0;
    if(ball < cube && ball < box && ball < jalusta){
        id = 1.0;
    }
    else if(cube < ball && cube < box && cube < jalusta){
        id = 2.0;
    }
    else if(jalusta < ball && jalusta < cube && jalusta < box){
        id = 3.0;
    }

    return vec2(scene, id);
}

vec3 normals(vec3 p){
    vec3 eps = vec3(EPSILON, 0.0, 0.0);
    return normalize(vec3(
        dist(p+eps.xyy).x-dist(p-eps.xyy).x,
        dist(p+eps.yxy).x-dist(p-eps.yxy).x,
        dist(p+eps.yyx).x-dist(p-eps.yyx).x
    ));
}

//Enhanced sphere tracing algorithm introduced by Mercury

// Sign function that doesn't return 0
float sgn(float x) {
	return (x < 0.0)?-1.0:1.0;
}

vec2 march(vec3 ro, vec3 rd){
    float t = 0.001;//EPSILON;
    float step = 0.0;

    float omega = 1.0;//muista testata eri arvoilla! [1,2]
    float prev_radius = 0.0;

    float candidate_t = t;
    float candidate_error = 1000.0;
    float sg = sgn(dist(ro).x);

    vec3 p = vec3(0.0);

	for(int i = 0; i < STEPS; ++i){
		p = rd*t+ro;
		float sg_radius = sg*dist(p).x;
		float radius = abs(sg_radius);
		step = sg_radius;
		bool fail = omega > 1. && (radius+prev_radius) < step;
		if(fail){
			step -= omega * step;
			omega = 1.;
		}
		else{
			step = sg_radius*omega;
		}
		prev_radius = radius;
		float error = radius/t;

		if(!fail && error < candidate_error){
			candidate_t = t;
			candidate_error = error;
		}

		if(!fail && error < PIXELR || t > FAR){
			break;
		}
		t += step;
	}
    //discontinuity reduction
    float er = candidate_error;
    for(int j = 0; j < 6; ++j){
        float radius = abs(sg*dist(p).x);
        p += rd*(radius-er);
        t = length(p-ro);
        er = radius/t;

        if(er < candidate_error){
            candidate_t = t;
            candidate_error = er;
        }
    }
	if(t <= FAR || candidate_error <= PIXELR){
		t = candidate_t;
	}

    p = ro+rd*t;
    float id = dist(p).y;

	return vec2(t, id);
}

//returns material of the object hit
// emissive color is xyz, and reflectance w
vec4 getMaterial(float obj, vec3 p){
    vec3 base = vec3(0.0);
    float reflectance = 0.0;
    if(obj == 0.0){
        if(p.y > 9.99||  p.y < -9.99){

            vec2 m = mod(p.xz, 4.0) - vec2(2.0, 2.0);
    		base =  m.x*m.y > 0.0 ? vec3(0.0) : vec3(0.8, 0.8, 0.7);
            reflectance = 0.1;
        }
        else{
            base = vec3(0.0);
        	reflectance = 0.1;
        }
    }
    else if(obj == 1.0){
        base = vec3(5.0);
        reflectance = 2.0;
    }
    else if(obj == 2.0){
        base = vec3(0.65, 0.7, 0.75);
        reflectance = 0.1;
    }
    else if(obj == 3.0){
        base = vec3(0.02, 0.02, 0.02);
        reflectance = 0.1;
    }
    return vec4(base, reflectance);
}

vec3 render(vec3 o, vec3 d, vec2 uv){

    vec3 ro = o;
    vec3 rd = d;

    vec3 pixel_color = vec3(0.0);
    vec3 absorption_factor = vec3(1.0);

    for(int i = 0; i < BOUNCES; ++i){
        vec2 t = march(ro, rd);
        vec3 p = ro+rd*t.x;

        if(t.y < 0.0 || t.x > FAR){
            break;
        }

        //material.xyz == emissive
        //material.w == reflectance
        vec4 material = getMaterial(t.y, p);
        pixel_color += material.xyz * absorption_factor;
        absorption_factor *= material.w;

        vec3 n = normals(p);
        ro = p+(n*0.02);
        if(p.x > 9.99 || p.x < -9.99){
            rd = reflect(rd,n);
        }
        else if(t.y == 3.0){
            rd = reflect(rd,n);
            //Thanks to fizzer to introducing this skew thing! :)
            rd = normalize(rd + (hash33(vec3(uv, float(i))) - 0.5)*0.1); 
        }
        else{
        	rd = lambertNoTangent(n, hash23(vec3(uv, float(i)+iTime*0.01)));
        }

    }

    return pixel_color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 q = -1.0+2.0*uv;
    q.x *= iResolution.x/iResolution.y;

    vec3 ro = vec3(9.5*cos(iTime*0.2), -1.0+sin(iTime*0.1), 9.5*sin(iTime*0.2));
    vec3 rt = vec3(0.0, -3.5+cos(iTime*0.2), 0.0);

    vec3 z = normalize(rt-ro);
    vec3 x = normalize(cross(z, vec3(0.0, 1.0, 0.0)));
    vec3 y = normalize(cross(x, z));

    vec3 color = vec3(0.0);

    for(float i = 0.0; i < SAMPLES; ++i){
    	vec3 rd = normalize(mat3(x, y, z)*vec3(q, radians(90.0)));
    	color += render(ro, rd, uv);
    }
    color /= SAMPLES;

    color = pow(color, 1.0/vec3(2.2));

	fragColor = vec4(color, 1.0);
}
