const int iterations=22;
const float speedCoef = .9;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //zVector
	vec2 z = fragCoord.xy / iResolution.xy*1.5;
	z.y*=iResolution.y/iResolution.x;
    //Gestion rotation
	z=vec2(z.x*cos(iTime*speedCoef)-z.y*sin(iTime*speedCoef),z.y*cos(iTime*speedCoef)+z.x*sin(iTime*speedCoef));
    //Synchro musique
    z/= 1.2 + sin(iChannelTime[0]*.99);
    //DÃ©placement camÃ©ra
    z+=vec2(sin(iChannelTime[0]*.5),cos(iChannelTime[0]*.5));
    //cVector
	vec2 c=vec2(0.1, .1);
	float average=0.;
	float l=length(z);
	float prevl;
    //iterationZ
	for (int i=0; i<iterations; i++) 
	{	
		z=abs(z)/dot(z,z) -c; 
		prevl=l;
		l=length(z);
		average+=abs(l-prevl);
	}
    //Occurences itÃ©rations
	average/=float(iterations) * 666.;
	//DÃ©finition couleurs
    vec3 colorsDef=vec3(0.5,0.1,.62);
	vec3 finalColor;	
    //Affection couleurs
	finalColor.r = (fract(float(average)/colorsDef.r));
	finalColor.g = (fract(float(average)/colorsDef.g));
	finalColor.b = (fract(float(average)/colorsDef.b));

	fragColor = vec4(finalColor,1.0);
}
