const vec4 Green = vec4(0.1, 0.7, 0.2, 1.0);
const vec4 White = vec4(1.0);
const vec4 Black = vec4(0.0, 0.0, 0.0, 1.0);
const float RadiusPx = 10.0;
const float SpacePx = 50.0;

// Distance in pixels
float circleDist(vec2 p, vec2 center) {
    return distance(center, p) - RadiusPx;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // uv Oâ€¦1
	vec2 uv = fragCoord.xy / iResolution.xy;

    // Find the nearest center
    vec2 center = SpacePx*floor(fragCoord.xy/SpacePx)+SpacePx/2.0;
	vec4 dotColor = mix(White, Green, clamp(circleDist(fragCoord.xy, center), 0.0, 1.0));

    // Vignetting
    fragColor = mix(dotColor, Black, 0.3*distance(uv, vec2(0.5)));
}
