#define SAMPLES 11.0
#define STEP_DISTANCE 10.0
#define NOISE_SPREAD 0.002
float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}
float raster(vec2 uv, float time){
	return floor((1.+atan(uv.x,uv.y+iTime)+0.6*sin(iTime+uv.x+uv.y))*length(uv+vec2(sin(time)*.25)+vec2(-.5))*4.);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 uv = fragCoord.xy / iResolution.xy;
    float color = 0.0;
    for(float i=0.0; i<SAMPLES; i++){
        uv = fragCoord.xy / iResolution.xy;
    	uv += vec2(NOISE_SPREAD)*vec2(1.0 - 0.5 * rand(uv*iTime) , 1.0 - 0.5 * rand(uv*iTime*1.12313) );
        color += raster(uv, iTime-i/(SAMPLES*STEP_DISTANCE))/SAMPLES;
    }
	fragColor = vec4(vec3(color),1.0);
}
