#define PI 3.14159265359
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 p = vec2(fragCoord.xy / iResolution.xy);
	vec2 r =  2.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;
	float t = iTime * 10.;

    r = r * 10.0;

    float v1 = sin(r.x + t);
    float v2 = sin(r.y + t);
    float v3 = sin(r.x + r.y + t);
    float v4 = sin(length(r) + 1.7 * t);
	float v = (v1+v2+v3+v4);

	vec3 ret;

    float rings = 8.;
    float minRings = -rings;

    float rp = (1. + 0.5 * sin(t + PI)) * PI;
    float bp = (1. + 0.5 * sin(t + 0.5 * PI)) * PI;
    float gp = (1. + 0.5 * sin(t)) * PI;
    v *= (rings + minRings + rings * sin(t));
	ret =  vec3(sin(v + rp), sin(v + bp), sin(v + gp));
	ret = 0.5 + 0.5 * ret;

    vec3 pixel = ret;
    fragColor = vec4(pixel, 1.);
}
