// Voronoi Cells - written 2015 by Jakob Thomsen
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

vec2 refl2(vec2 v)
{
    return abs(2.0 * fract(v * 0.5) - 1.0);
}

vec3 voronoi(vec2 pos)
{
    float t = 0.1 * iTime;
    int nearest = 0;
    float mindist = 1000.0;
    const int n = 100;
    for(int i = 0; i < n; i++)
    {
        //vec2 pt = texture(iChannel0, vec2(float(i) / float(n), t)).xy;
        vec2 p = texture(iChannel0, vec2(float(i) / float(n), 0.0)).xy;
        vec2 v = texture(iChannel0, vec2(float(i) / float(n), 0.5)).xy;
        vec2 pt = refl2(p + v * t);
        float d = length(pos - pt);
        if(d < mindist)
        {
            mindist = d;
            nearest = i;
        }
    }

    return texture(iChannel1, vec2(float(nearest) / float(n))).rgb;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = vec4(voronoi(uv).rgb, 1.0);
}
