float PI = 3.1415926535897;

float radius = 1.0;
float width = 0.3;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scaleX = 1.0/1.0;
    float scaleY = 1.0/1.0;

    vec2 center = vec2(0.5, 0.5);
    vec2 pt = vec2(fragCoord.x / iResolution.x / scaleX, fragCoord.y / iResolution.y / scaleY);
    float angle = atan((pt.y - center.y) / (pt.x - center.x));
    float l = distance(center, pt);

    float _radius = radius * 0.26 + cos(angle * 20.0 + iTime * 3.0 + l * 100.0) * 0.01;
    float _width = clamp(radius, 0.0, 1.0) * width + cos(angle * 14.0 + iTime * 10.0 + l * 100.0) * 0.03;
    if (_width < 0.0)
        _width = 0.0;

    _radius = _radius + cos(iTime * 3.0) * 0.02;
    if (_radius < 0.0)
        _radius = 0.0;

	float w2 = _width / 2.0;
    float v = cos(clamp(l - _radius, -w2, w2) / w2 * PI / 2.0);

    float light = clamp(v, 0.0, 1.0);

    fragColor = vec4(light*0.0, light, light*1.5, light);
}
