void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv              = fragCoord.xy / iResolution.xy;

    float minHeight      = 0.05;
    float maxHeight      = minHeight + 0.3;
    float translation    = iTime * 0.1;
    float wavesPerScreen = 10.0;

    float sinval         = minHeight + (0.5 + 0.5 * sin((translation + uv.x) * wavesPerScreen*2.0*3.14)) * maxHeight;

    if (sinval < uv.y)
		fragColor = vec4(0.5, 0.1, 0.2,1.0);
    else
        fragColor = vec4(0.3, 0.4, 1.0, 1.0);
}
