renderpass.code:
float d( in vec2 x )
{
    return abs(length(x)-5.0);
}

float color( in vec2 x )
{
    return smoothstep( 0.0, 0.4, d(x) );
}

vec2 org = vec2( -1.5, 0.0 );

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ( fragCoord.xy*2.0 - iResolution.xy ) / min( iResolution.x, iResolution.y );
    vec2 mouse = (iMouse.xy*2.0 - iResolution.xy ) / min( iResolution.x, iResolution.y );
    vec2 ray = normalize(mouse-org);

    vec2 diff = uv-org;
    float distL = length(diff-dot(diff, ray)*ray);

    uv *= 20.0;
    org *= 20.0;

    vec3 col = vec3( color(uv) );

    col *= vec3( smoothstep( 0.0, 0.02, distL ) );

    vec2 cur = org;
    for( int i = 0; i < 16; ++i )
    {
        float dist = d( cur );

        if( dist < 0.001 )
        {
          break;  
        }

        col *= vec3( smoothstep( 0.0, 0.4, abs(length(uv-cur)-1.0) ) );
        col *= vec3( smoothstep( 0.0, 0.4, abs(length(uv-cur)-dist) ), 1.0, 1.0 );

        cur += dist * ray;
    }

    fragColor = vec4( col , 1.0 );
    //fragColor = vec4( vec3(d(uv)/10.0), 1.0 );
}
