vec2 squ(vec2 c){return vec2(c.x*c.x-c.y*c.y,2.*c.x*c.y);}
float pyt=3.1415926*2./3.;
vec2 zoompt=vec2(-0.6586,0.3775);
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    float jTime=iTime-30.*floor(iTime/30.);
	vec2 uv = (4.*fragCoord.xy / iResolution.x-vec2(2.,1.1))/pow(1.5,jTime+0.5)+zoompt;

    vec2 c=uv;
    for(int i=0;i<20+int(jTime*2.5);i++){
    c=squ(c)+uv;
     fragColor = vec4(0.,0.,0.,1.);
    if (length(c)>2.){
        float f=float(i)*0.3;
        fragColor = vec4(sin(f),sin(f+pyt),sin(f-pyt),1.0);
        break;


    }

    }}
