float size = 25.0;
vec3 color1 = vec3(0.4, 0.4, 0.4);
vec3 color2 = vec3(0.6, 0.6, 0.60);

bool even(float w){
    return mod(floor(w / size), 2.0) == 0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    bool yEven = even(fragCoord.y);
    bool xEven = even(fragCoord.x);
    if((yEven && xEven) || (!yEven && !xEven)){
        fragColor = vec4(color1, 1.0);
    }else{
        fragColor = vec4(color2, 1.0);

    }
}
