#define MULTI_SAMPLE_4 0
#define MULTI_SAMPLE_8 0
#define MULTI_SAMPLE_3 1
#define MULTI_SAMPLE_2 0


const float tor_rad1 = 0.5;
const float tor_rad2 = 0.2;

vec4 background(vec3 orig, vec3 dir, float bounced) {
    if (bounced > 0.5) {
	    return texture(iChannel1, dir);
    }
    return texture(iChannel2, dir);
}

float torus_sdf(in vec3 p) {
	vec2 to_centerline =
        vec2(abs(length(p.xz) - tor_rad1), p.y);
    return length(to_centerline) - tor_rad2;
}

vec3 raymarch(in vec3 orig, in vec3 dir) {
	float result = 0.0;
    vec3 p = orig;
    for (int i = 0; i < 32; ++i) {
    	float d = torus_sdf(p);
        result = result + d;
        p = p + d * dir;
        if (d < 1.0e-4) {
            return p;
        }
    }
    return p;
}

vec3 tor_grad(in vec3 point) {
	vec2 ref_point = normalize(point.xz) * tor_rad1;
    return normalize(point - vec3(0.0, ref_point).yxz);
}

vec3 tor_tangent(in vec3 point) {
    vec2 ref_point = normalize(point.xz) * tor_rad1;
    return vec3(ref_point, 0.0).zyx * vec3(-1.0, 0.0, 1.0);
}

vec2 tor_angles(in vec3 point) {
	float theta1 = atan(point.z, point.x);
    float theta2 =
        atan(point.y, length(point.xz) - tor_rad1);
	return vec2(theta1, theta2);
}

vec3 tor_norm(in vec3 point, out float glow_mag) {
    vec3 n = tor_grad(point);
    vec2 uv = vec2(0.5) + tor_angles(point) / (2.0 * 3.141592654);
    uv.x = mod(uv.x * 4.0, 1.0);
	vec3 t1 = tor_tangent(point);
    vec3 t2 = normalize(cross(t1, n));

    float h = 1.0 / min(iResolution.x, iResolution.y);
    float img_samp = length(texture(iChannel0, uv).rg);
    glow_mag = img_samp;
    vec2 img_grad =
        vec2(length(texture(iChannel0, 
                            mod(uv + vec2(h, 0.0), vec2(1.0))).rg) - img_samp,
             length(texture(iChannel0,
                            mod(uv + vec2(0.0, h), vec2(1.0))).rg) - img_samp) / h;
	return n + 0.1 * (img_grad.x * t1 + img_grad.y * t2);
}


vec4 getSample(in vec2 fragCoord )
{
	vec2 uv = (2.0 * fragCoord.xy - iResolution.xy) / min(iResolution.x, iResolution.y);

    vec4 color_mul = vec4(1.0);
    vec4 color_add = vec4(0.0);

    float stheta = sin(0.125 * iTime);
    float ctheta = cos(0.125 * iTime);
    float pitch = 0.5;
    mat3 turn_mat = 
        mat3(ctheta, 0.0, stheta,
             0.0, 1.0, 0.0,
             -stheta, 0.0, ctheta) *
        mat3(1.0, 0.0, 0.0,
                         0.0, cos(pitch), sin(pitch),
                         0.0, -sin(pitch), cos(pitch));

    vec3 ray_orig = turn_mat * vec3(0.0, 0.0, -1.0);

    vec3 ray_dir = normalize(turn_mat * vec3(uv, 1.0));

    float bounced = 0.0;

	vec3 hit = raymarch(ray_orig, ray_dir);
    if (torus_sdf(hit) < 0.1) {
        float glow;
    	vec3 n = tor_norm(hit, glow);
        ray_dir = normalize(reflect(ray_dir, n));
        ray_orig = hit;

        color_add.b = 0.5 * smoothstep(0.3, 0.4, glow);
        bounced = 1.0;

    }

	return color_mul * background(ray_orig, ray_dir, bounced) + color_add;
}


vec2 offsets[8] = vec2[8](vec2(-0.4, -0.4), 
                         vec2(-0.45, 0.1),
                         vec2(-0.4, 0.4),
                         vec2(0.1, 0.45),
                         vec2(0.4, 0.4),
                         vec2(0.45, -0.1),
                         vec2(0.4, -0.4),
                         vec2(0.0, 0.0));


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 result = vec4(0.0);

    float nsamps = 0.0;

    result += getSample(fragCoord + offsets[0]);
    nsamps += 1.0;

#if MULTI_SAMPLE_8
    result += getSample(fragCoord + offsets[1]);
    result += getSample(fragCoord + offsets[2]);
    result += getSample(fragCoord + offsets[3]);
    result += getSample(fragCoord + offsets[4]);
    result += getSample(fragCoord + offsets[5]);
    result += getSample(fragCoord + offsets[6]);
    result += getSample(fragCoord + offsets[7]);
    nsamps += 7.0;
#elif MULTI_SAMPLE_4
    result += getSample(fragCoord + offsets[2]);
    result += getSample(fragCoord + offsets[4]);
    result += getSample(fragCoord + offsets[6]);
    nsamps += 3.0;
#elif MULTI_SAMPLE_3
    result += getSample(fragCoord + offsets[3]);
    result += getSample(fragCoord + offsets[6]);
    nsamps += 2.0;
#elif MULTI_SAMPLE_2
    result += getSample(fragCoord + offsets[4]);
    nsamps += 1.0;
#endif



    fragColor = result / nsamps;
}

vec2 to_uv(in vec2 in_pixels) {
    return mod(vec2(1.0) + (in_pixels / iResolution.xy), vec2(1.0));
}

vec2 fetch_dir(in vec2 in_pixels) {
    vec2 dir = texture(iChannel0, to_uv(in_pixels)).rg;
    dir = step(vec2(0.5), dir) * vec2(step(dir.y, dir.x), step(dir.x, dir.y));
    return dir;
}

void mainImage( out vec4 fragColor, in vec2 p )
{
    const float refresh_period = 15.0;
    float t = iTime - mod(iTime, refresh_period);
    mat2 rotator = mat2(cos(t), sin(t),
                          -sin(t), cos(t));
    vec2 dir = step(vec2(0.95), texture(iChannel1, to_uv(rotator * p)).rg);
    dir = vec2(step(dir.y, dir.x), step(dir.x, dir.y)) * dir;


    if (mod(iTime, refresh_period) < 0.1) {
      fragColor = vec4(dir, 0.0, 1.0);
        return;
    }

    if (dot(dir, dir) < 0.5) {
	    dir = step(vec2(0.5), texture(iChannel0, to_uv(p)).rg);
    }

     if (dot(dir, dir) < 0.01) {
    	vec2 dir1 = vec2(1.0, 0.0);
    	vec2 dir2 = dir1.yx;


    	float val1 = max(abs(dot(dir1, fetch_dir(p + dir1))),
                     	abs(dot(dir1, fetch_dir(p - dir1))));


    	float val2 = max(abs(dot(dir2, fetch_dir(p + dir2))),
        	             abs(dot(dir2, fetch_dir(p - dir2))));


        if (val1 > val2) {
            val2 = 0.0;
        } else {
            val1 = 0.0;
        }

        val1 = step(0.9, val1);
        val2 = step(0.9, val2);


    	dir = mix(dir, dir1, val1);
    	dir = mix(dir, dir2, val2);
    }

    dir = step(vec2(0.5), dir) * vec2(step(dir.y, dir.x), step(dir.x, dir.y));

    fragColor = vec4(dir,0.0,1.0);
}

vec2 to_uv(in vec2 in_pixels) {
    return in_pixels / iResolution.xy;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pix_delta = 1.0 / iResolution.xy;
    vec2 uv = fragCoord * pix_delta;
    vec4 result = texture(iChannel0, uv);
    result = result + texture(iChannel0, uv + pix_delta * vec2( 1.0, 0.0));
    result = result + texture(iChannel0, uv + pix_delta * vec2(-1.0, 0.0));
    result = result + texture(iChannel0, uv + pix_delta * vec2(0.0,  1.0));
    result = result + texture(iChannel0, uv + pix_delta * vec2(0.0, -1.0));
    fragColor = mix(0.2 * result, texture(iChannel1, uv), 0.05);
}
