// http://lodev.org/cgtutor/juliamandelbrot.html

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = -1.0+2.0*fragCoord.xy / iResolution.xy;
	pos.x *= iResolution.x/iResolution.y;

    vec2 c = vec2(-0.7, 0.27015);
    vec2 z = pos;
    vec4 col;

    for (int i = 0; i < 50; i++)
    {
        z = vec2(z.x * z.x - z.y * z.y, 2.0f * z.x * z.y) + c;

        if (dot(z, z) > 4.0) 
            break;
    }

    fragColor = vec4(clamp(z, 0.0, 1.0), 0.5, 0.0);
}
