float cuad(in vec2 p, float x, in vec2 pos){

    vec2  tr,bl;

    p = p-pos;

    tr = step(x, p);
    bl= step(x, 1.0-p);

    return tr.x*tr.y*bl.x*bl.y;
}

float rect(in vec2 p, float x, float y,in vec2 pos){

    float  tr,bl,tra,bla;

    p = p-pos;

    tr = step(x, p.x);
    bl= step(x, 1.0-p.x);

    tra = step(y, p.y);
    bla = step(y, 1.0-p.y);

    return tr*tra*bl*bla;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = 2.0*uv - 1.0;
    p.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.0);

    float r1 = rect(p,0.3, 0.2, vec2(-2.1,0.2));
    float r2 = rect(p,0.3, 0.2, vec2(-1.644,0.2));
    float r3 = rect(p,-0.3, 0.2, vec2(-0.584,0.2));
    float c3 = cuad(p,0.2, vec2(0.58, 0.2));
    float r4 = rect(p,0.3,0.2, vec2(1.14,0.2));

    float r5 = rect(p,0.3, 0.2, vec2(-2.1,-0.45));
    float r6 = rect(p,0.3, 0.2, vec2(-1.644,-0.45));
    float r7 = rect(p,-0.3, 0.2, vec2(-0.584,-0.45));
    float c2 = cuad(p,0.2, vec2(0.58, -0.45));
    float r8 = rect(p,0.3,0.2, vec2(1.14,-0.45));

    float r9 = rect(p,-0.158, 0.0, vec2(-2.1,-1.31));
    float r10 = rect(p,-0.3, 0.1, vec2(-0.582,-1.21));
    float r11 = rect(p,0.2, 0.1, vec2(0.582,-1.21));
    float r12 = rect(p,0.3, 0.1, vec2(1.14,-1.21));

    col += vec3(c3)*vec3(1.0,0.9,0.8);
    col += vec3(r1)*vec3(0.5,0.0,0.0);
    col += vec3(r2)*vec3(0.5,0.0,0.0);
    col += vec3(r3)*vec3(1.0,0.9,0.8);
    col += vec3(r4)*vec3(1.0,0.9,0.0);

    col += vec3(c2)*vec3(1.0,0.9,0.8);
    col += vec3(r5)*vec3(1.0,0.9,0.8);
    col += vec3(r6)*vec3(1.0,0.9,0.8);
    col += vec3(r7)*vec3(1.0,0.9,0.8);
    col += vec3(r8)*vec3(1.0,0.9,0.8);

    col += vec3(r9)*vec3(1.0,0.9,0.8);
    col += vec3(r10)*vec3(1.0,0.9,0.8);
    col += vec3(r11)*vec3(1.0,0.9,0.8);
    col += vec3(r12)*vec3(0.0,0.6,0.9);

	fragColor = vec4(col,1.0);
}
