#define TOP vec2(0,1)
#define RIGHT vec2(1.5,0)

vec3 sample(vec2 uv, vec2 offset) {
    float time_factor = iTime * 0.15;
	vec3 col = texture(iChannel0, uv + offset * time_factor).rgb;
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;

	vec3 top = sample(uv, TOP);
    vec3 bottom = sample(uv, -TOP);
    vec3 right = sample(uv, RIGHT);
    vec3 left = sample(uv, -RIGHT);

    fragColor.rgb = max(top, max(bottom, max(right, left)));
}
