// Settings
const float FOV_DEG = 80.0f;
const int MAX_STEPS = 300;
const vec3 LIGHT_DIR = vec3(1.0,1.0,-1.0);

// Consts / Enumerations 
const float EPS = 0.0001f;
const float DEG_TO_RAD = 0.0174533f;
const float M_PI = 3.14159265358979323846;
const int FLOOR_PLANE = 1;
const int OBJECT = 2;
const int OBJECT2 = 3;
const int NO_HIT = 0;

///////////////////////////////////////////////////////////

struct Ray
{
    vec3 Origin;
    vec3 Direction;
};

//Utils
mat3 CreateRotationX(float rotation)
{
    mat3 rotateX =
     mat3(
        1.0, 0.0, 0.0,
		0.0, cos(rotation), -sin(rotation),
		0.0, sin(rotation), cos(rotation)
    );

    return rotateX;
}

mat3 CreateRotationY(float rotation)
{
    mat3 rotateY = 
     mat3(
        cos(rotation), 0.0, sin(rotation),
		0.0, 1.0, 0.0, 
		-sin(rotation), 0.0, cos(rotation)
    );

    return rotateY;
}

mat3 CreateRotationZ(float rotation)
{
    mat3 rotateZ =
    mat3(
        cos(rotation), -sin(rotation), 0.0,
		sin(rotation), cos(rotation), 0.0,
		0.0, 0.0, 1.0
    );

    return rotateZ;
}

// SDFs
float SphereSDF(vec3 p)
{
    return length(p)-1.1;
}

float TorusSDF(vec3 p, float R, float r)
{  
    float xy = length(p.xy) - R;
    float z = length(vec2(xy,p.z))-r; 
    return z;
}

float BoxSDF(vec3 p, vec3 b)
{
    vec3 d = abs(p) - b;
    return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0))-0.1;
}

float CylinderSDF(vec3 p,float radius)
{   
    float d = length(p.xz)-radius;
    d = max(d,p.y-1.0);
    d = max(d,-p.y-1.0);
    return d;
}

float PlaneSDF(vec3 p, float y)
{
    return p.y+y;
}

vec3 RepeatX(vec3 p, float offset)
{
    p.x = mod(p.x,offset) - offset/2.0;
    return p;
}

vec3 Twist(vec3 p, float amount)
{
    float c = cos(amount*p.y);
    float s = sin(amount*p.y);

    // Create 2D rotation matrix
    mat2 rm = mat2(c,-s,s,c);
    p.xz *=rm;
    return p;
}

//TODO: TASK1
void Phong(vec3 L, vec3 N, vec3 V, float sh, out float d, out float s)
{
    d = 0.85*max(dot(L, N), 0.0);
    vec3 R = (vec3(2.0)*N*dot(L, N))-L;
    s = 0.15*max(pow(dot(R, V), 5.0), 0.0);
}

//Main scene distance field function
float CombineSDF(vec3 p, inout int closest)
{
      vec3 pn = p*vec3(1.0,0.6,1.0);;
      pn =  Twist(pn+vec3(0,0,-0.3),sin(iTime)*0.5);//CreateRotationY(iTime)* vec4(p,0);
      float sd = SphereSDF(pn.xyz);  

      sd = max(BoxSDF(pn.xyz,vec3(0.95)),-sd); 
      float  torus = TorusSDF(CreateRotationY(iTime)*(p + CreateRotationY(iTime*0.3)*vec3(0.0,0.0,2.5)) ,0.9f,0.4f);
      sd = min(torus,sd);      
      float pd =  PlaneSDF(p,23.0); 
      float fin = min(sd,pd);
      closest = OBJECT;
      if(fin == pd)
      { closest = FLOOR_PLANE;}
      else if(fin == torus) 
      {closest = OBJECT2;}
      return fin;
}

vec3 GetNormal(vec3 p)
{
    int dummy = 0;
    return 
    normalize(vec3(
		CombineSDF(vec3(p.x + EPS, p.y, p.z), dummy) - CombineSDF(vec3(p.x - EPS, p.y, p.z), dummy),
		CombineSDF(vec3(p.x, p.y + EPS, p.z), dummy) - CombineSDF(vec3(p.x, p.y - EPS, p.z), dummy),
		CombineSDF(vec3(p.x, p.y, p.z + EPS), dummy) - CombineSDF(vec3(p.x, p.y, p.z - EPS), dummy)
		));
}

float Trace(Ray r,inout int hit)
{
    hit = 0;
    float t = 0.0f;

    for(int i =0; i < MAX_STEPS; ++i)
    {
       vec3 p = r.Origin + r.Direction * t;
       int closest = 0;     
       float sd = CombineSDF(p, closest);

       if(sd < EPS)
       {
          hit = closest;
          return t;
       }
       t += sd;
    }
    return 0.0f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Frag coord normalization 
    vec2 uv = fragCoord.xy/iResolution.xy;
    // Make center of the screen be (0,0)
    uv = uv * 2.0 - 1.0; 
    // Aspect ratio
    uv.x *= iResolution.x/iResolution.y;

    //FOV
    float fovDist = tan(FOV_DEG  *0.5 * M_PI / 180.0); 
    uv *= fovDist;

    //TODO: TASK2
    //CHANGE CAMERA POSITION AND ORIENTATION HERE:

    Ray r;
    r.Direction = normalize(vec3(uv,1.0)*CreateRotationX(float(35.0/180.0*M_PI)));
    r.Origin = vec3(0,4.0,-5.0);

    int hit = NO_HIT;   // indicates type of the object hit
    float t = Trace(r,hit);  
    vec3 hitAt = r.Origin + r.Direction * t;
    vec3 N = GetNormal(hitAt);

    float diff,spec = 0.0;
    vec3 col = vec3(0); 
    float stm = sin(iTime);

    if(hit == OBJECT) // Block
    {        
        Phong(LIGHT_DIR,N,-r.Direction,50.0,diff,spec);
        col =  vec3(0.2,0.04,0.2)+ vec3(0.5,0.0,0.5) * diff + vec3(1.0) * spec;
    }
    else if(hit == OBJECT2) // Torus
    {
        Phong(LIGHT_DIR,N,-r.Direction,50.0,diff,spec);
        vec3 baseCol = vec3(clamp(cos(hitAt.y),0.0,1.0), clamp(sin(hitAt.y),0.0,1.0),0.0);
        col =  0.3 * baseCol + 0.7 * baseCol * diff + vec3(1.0)*spec; 
    } 
    else if(hit == FLOOR_PLANE) // Floor
    {
        float ar = iResolution.x/iResolution.y;
        vec2 planeUV = vec2(80.0+hitAt.x,hitAt.z)/200.0;
        vec3 uplines = vec3(pow(1.0-sin(hitAt.x / 3.0f), 0.5f));
        float ctm = float(cos(iTime));
        float sidetime = 0.4f+(stm/3.0f);
        float sidefloat = pow(1.0-sin(hitAt.z/5.0f), 2.0f)+0.3f + sidetime;
        vec3 sidelines = vec3(sidefloat, sidetime, sidefloat);
        col = texture(iChannel0, planeUV).xyz*uplines*sidelines*20.0/length(vec2(pow(sin(iTime), 2.0)*hitAt.x, pow(cos(iTime), 2.0)*hitAt.z));

        // ADD FLOOR PATTERNS

    }

    else // NO HIT - BACKGROUND COLOR
    {
      col = 0.5 + 0.5 * cos(uv.xyx + vec3(0,2.3,3.5));
    }
    //TODO: TASK3
    //SCREEN SPACE EFFECTS

   // Output to screen
    //fragColor = vec4(col, 1.0);
    float graphscalar = pow((fragCoord.x/iResolution.x),2.8);
    vec3 saturation = (1.0-graphscalar) * vec3(length(col));
    vec3 blackwhite = graphscalar * col;
    fragColor = vec4((saturation+blackwhite)/2.0,1.0);
}
