vec2 F(vec2 c, vec2 z)
{
    return vec2(z.x*z.x - z.y*z.y + c.x, 2.0*z.x*z.y + c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 xy = fragCoord.xy; 

    xy.x = (xy.x / iResolution.x - 0.5) * 2.0; 
    xy.y = (xy.y / iResolution.y - 0.5) * 2.0; 

    float i = 0.0;
    vec2 z = xy;
    float ITERS = 100.0;
    float R = 3.0;
    vec2 c = vec2(0.3,0.5);
    while (i <= ITERS){
        z = F(c,z);
        if (z.x*z.x + z.y*z.y > R*R){
            break;
        }
        i++;
    }
    vec3 xyz;
    if (iFrame > 0) {
    	xyz = texture(iChannel0, vec2(0.0, 0.0)).xyz;
        xy = xyz.z * xy; 
		xy = xy - xyz.xy; 
    }

    fragColor = vec4(vec3(i/ITERS), 1.0);

}

const float KEY_W = 87.5 / 256.0;
const float KEY_A = 65.5 / 256.0;
const float KEY_S = 83.5 / 256.0;
const float KEY_D = 68.5 / 256.0;
const float KEY_E = 69.5 / 256.0;
const float KEY_Q = 81.5 / 256.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = fragCoord / iResolution.xy;
  	float z = 2.0;
    vec3 xyz;
   pos = z * pos;
    if (iFrame > 0) {
  		xyz = texture(iChannel1, vec2(0.0, 0.0)).xyz;
        pos = xyz.xy;
        z = xyz.z;
    }
    if (texture(iChannel0, vec2(KEY_E, 0.25)).x > 0.5)
        z = z + 0.05 * z;
    if (texture(iChannel0, vec2(KEY_Q, 0.25)).x > 0.5)
    	z = z - 0.05 * z;

    if (texture(iChannel0, vec2(KEY_A, 0.25)).x > 0.5)
        pos.x = pos.x + 0.05 * z;
     if (texture(iChannel0, vec2(KEY_W, 0.25)).x > 0.5)
        pos.y = pos.y - 0.05 * z;
	if (texture(iChannel0, vec2(KEY_S, 0.25)).x > 0.5)
		pos.y = pos.y + 0.05 * z;
	if (texture(iChannel0, vec2(KEY_D, 0.25)).x > 0.5)
		pos.x = pos.x - 0.05 * z;

    fragColor = vec4(pos, z, 0.0);
}
