void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 10.0;
    bool allQuadrants = true;

	vec2 uv = fragCoord.xy / iResolution.xy * scale - scale / 2.0 * vec2(1.0, float(allQuadrants));

    float x = uv.x;
    float y = uv.y;

    float width = 0.07;
    vec2 bounds = vec2(-width, width) + y;

    float dep = x * x * x;
    float test = float(dep > bounds.x && dep < bounds.y);

	fragColor = vec4(test);
}
