void mainImage( out vec4 f, vec2 g )
{
    f -= f;

    g = (g+g-iResolution.xy)/iResolution.y*2.2;
    g /= dot(g,g)*.6 * (sin(iDate.w)*.5+.75);

    vec2 s = 2.*mod(floor(g/=2.), 2.) - 1.,  u;

    for (int i=0; i<4; i++)

        u = fract(g = vec2(-g.y,g.x)),

        f += step(
            clamp(1.5 * cos( 8.*atan(u.x,u.y+1e-6) + s.x*s.y*iDate.w*5. + 1.6) 
                  + 6., 5., 7.), 
            length(u) * 12.3
                 ) / 4.,

        s.x = -s.x;

}
